CREATE OR REPLACE FUNCTION nut_permite_ver_local_paciente ( ie_tipo_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(1);
ie_local_pac_refeicao_w varchar(1);
ie_local_pac_planej_w	varchar(1);

BEGIN

ds_retorno_w := 'N';

if (ie_tipo_p in (1,2)) then

	select	max(ie_local_pac_refeicao),
		max(ie_local_pac_planej)
	into STRICT	ie_local_pac_refeicao_w,
		ie_local_pac_planej_w
	from	parametros_nutricao
	where	cd_estabelecimento = obter_estabelecimento_ativo;

	if (ie_tipo_p = 1) then
		ds_retorno_w := ie_local_pac_refeicao_w;
	else
		ds_retorno_w := ie_local_pac_planej_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
