CREATE OR REPLACE FUNCTION nut_status_card_ordem_prod ( nr_seq_card_dia_p bigint, dt_opcao_p timestamp) RETURNS varchar AS $body$
DECLARE


qtd_w	varchar(1);


BEGIN

    if (nr_seq_card_dia_p IS NOT NULL AND nr_seq_card_dia_p::text <> '') then
            select  CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
            into STRICT    qtd_w
            from    nut_ordem_prod_card a,
                    nut_ordem_prod b,
                    nut_cardapio_dia c
            where   a.nr_seq_ordem = b.nr_sequencia
            and     a.nr_seq_cardapio = c.nr_sequencia
            and     c.nr_sequencia = nr_seq_card_dia_p
            and     trunc(b.dt_ordem) =  trunc(dt_opcao_p)
            order   by b.dt_liberacao desc;
    end if;

return	qtd_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
