CREATE OR REPLACE FUNCTION nut_valida_cardapio_padrao ( nr_seq_cardapio_dia_p bigint default 0, ie_semana_p text DEFAULT NULL, ie_dia_semana_p text DEFAULT NULL, nr_seq_grupo_producao_p bigint default 0, cd_dieta_p bigint default 0, nr_seq_local_p bigint default 0, nr_seq_opcao_p bigint default 0, dt_vigencia_inicial_fun_p timestamp DEFAULT NULL, dt_vigencia_final_fun_p timestamp DEFAULT NULL, nr_seq_servico_p bigint default 0) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	nut_cardapio_dia a
where	nr_sequencia <> nr_seq_cardapio_dia_p
and (ie_semana = ie_semana_p)
and (ie_dia_semana_p = ie_dia_semana)
and	nr_seq_local = nr_seq_local_p
and	a.nr_seq_servico = nr_seq_servico_p
and	dt_vigencia_inicial >= dt_vigencia_inicial_fun_p
and	dt_vigencia_final <= dt_vigencia_final_fun_p
and	((cd_dieta = cd_dieta_p) or (nr_seq_grupo_producao = nr_seq_grupo_producao_p) or  exists (SELECT	1
												from	nut_grupo_producao x,
													nut_grupo_producao_dieta k
												where	k.nr_seq_grupo_producao	= x.nr_sequencia
												and	x.nr_sequencia 		= nr_seq_grupo_producao_p
												and	a.cd_dieta 		= k.cd_dieta)
or	exists (	select	1
		from	nut_grupo_producao x,
			nut_grupo_producao_dieta k
		where	k.nr_seq_grupo_producao	= x.nr_sequencia
		and	k.cd_dieta 		= cd_dieta_p
		and	a.nr_seq_grupo_producao	= k.nr_seq_grupo_producao))
and	coalesce(ie_cardapio_padrao,'N') = 'S';

return	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
