CREATE OR REPLACE FUNCTION nut_verifica_material_estoque ( cd_material_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := '';


BEGIN
	if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
		select 	coalesce(b.ie_material_estoque,'S')
		into STRICT	ds_retorno_w
		from   	material a,
			material_estab b
		where  	a.cd_material = b.cd_material
		and    	b.cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
		and    	a.cd_material = cd_material_p;
	end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
