CREATE OR REPLACE FUNCTION obtain_active_convenio (cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_situacao_w	varchar(1) := 'I';


BEGIN
if (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then
	select	coalesce(max(ie_situacao), 'I')
	into STRICT	ie_situacao_w
	from	convenio
	where 	cd_convenio	= cd_convenio_p;
	
end if;

return ie_situacao_w;

end;
$body$
LANGUAGE PLPGSQL
