CREATE OR REPLACE FUNCTION obtain_admin_instr_count ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


  ds_retorno_w varchar(1 ) := 'N';
  nr_admin_ins_count_w bigint:= 0;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
 select count(*)
 into STRICT   nr_admin_ins_count_w
 from   CPOE_HEMOTERAPIA cm , blood_admin_instructions bai
 where  bai.CD_CPOE_ITEM_SEQ = cm.nr_sequencia
        and bai.cd_cpoe_item_seq= nr_sequencia_p;

    if (nr_admin_ins_count_w > 0) then
        ds_retorno_w := 'S';
    end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
