CREATE OR REPLACE FUNCTION obtain_mbs_numbers (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedure_w		varchar(10);

ds_return_w		varchar(2000) := '';

C01 CURSOR FOR

      SELECT a.CD_PROCEDIMENTO_PRINC from PROCEDIMENTO_PACIENTE a
		  where a.NR_SEQUENCIA = nr_atendimento_p and (a.CD_PROCEDIMENTO_PRINC IS NOT NULL AND a.CD_PROCEDIMENTO_PRINC::text <> '')
		  and a.IE_ORIGEM_PROCED = 20;


BEGIN

  open c01;

	loop

	fetch c01 into

		cd_procedure_w;

	EXIT WHEN NOT FOUND; /* apply on c01 */

	begin



	ds_return_w := cd_procedure_w || ',' || ds_return_w;



	end;

	end loop;

close c01;



ds_return_w := substr(ds_return_w, 1, length(ds_return_w) - 1);



return ds_return_w;



END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
