CREATE OR REPLACE FUNCTION obtain_price_rebate (cd_procedimento_p bigint) RETURNS bigint AS $body$
DECLARE



                                
vl_custo_operacional_w               preco_amb.vl_custo_operacional%type;


BEGIN

select    coalesce(max(x.vl_custo_operacional),0)
into STRICT        vl_custo_operacional_w
from      preco_amb x,
                              convenio_amb y,
                              convenio t
where   x.cd_edicao_amb = y.cd_edicao_amb
and        t.cd_convenio = y.cd_convenio
and        t.ie_tipo_convenio = 12 --- MEDICARE HEALT FUND
and        clock_timestamp() between y.dt_inicio_vigencia and coalesce(y.dt_final_vigencia,clock_timestamp())
and        x.cd_procedimento = cd_procedimento_p;

return vl_custo_operacional_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
