CREATE OR REPLACE FUNCTION obtain_procedures_encounter (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

cd_procedure_w		varchar(10);
ds_return_w		varchar(2000) := '';
C01 CURSOR FOR
    SELECT a.CD_PROCEDIMENTO
      from PROCEDIMENTO_PAC_MEDICO a, procedimento p 
      where a.NR_SEQUENCIA = nr_atendimento_p
        and a.cd_procedimento = p.cd_procedimento
        and a.IE_ORIGEM_PROCED = p.IE_ORIGEM_PROCED
        and p.IE_ORIGEM_PROCED = 19
;

BEGIN
  open c01;
	loop
	fetch c01 into
		cd_procedure_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ds_return_w := cd_procedure_w || ',' || ds_return_w;

	end;
	end loop;
close c01;

ds_return_w := substr(ds_return_w, 1, length(ds_return_w) - 1);

return ds_return_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
