CREATE OR REPLACE FUNCTION obtain_same_unid_med_inter ( ie_unidade_cal_p text, cd_material_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_showing_w			varchar(1);
cd_unidade_medida_w		material_conversao_unidade.cd_unidade_medida%type;
ie_unidade_med_inter_w	unidade_medida.ie_unidade_med_inter%type;

C01 CURSOR FOR
	SELECT	cd_unidade_medida
	from	material_conversao_unidade
	where	cd_material = cd_material_p
	and 	coalesce(ie_permite_prescr,'S') = 'S';


BEGIN

ie_showing_w := 'N';

open C01;
loop
fetch C01 into	
	cd_unidade_medida_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		
		select 	max(ie_unidade_med_inter)
		into STRICT	ie_unidade_med_inter_w
		from 	unidade_medida
		where 	cd_unidade_medida = cd_unidade_medida_w;
		
		if (coalesce(ie_unidade_med_inter_w::text, '') = '' and upper(cd_unidade_medida_w) = upper(ie_unidade_cal_p)) then
			ie_showing_w := 'S';
			exit;
		elsif (upper(ie_unidade_med_inter_w) = upper(ie_unidade_cal_p)) then
			ie_showing_w := 'S';
			exit;
		end if;
	end;
end loop;
close C01;

return	ie_showing_w;

end;
$body$
