CREATE OR REPLACE FUNCTION obtain_user_locale (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


tag_pais_w           varchar(15);
cd_estabelicimento_w bigint;


BEGIN

select	max(ds_locale)
into STRICT	tag_pais_w
from	user_locale
where	nm_user = nm_usuario_p;

if (coalesce(tag_pais_w::text, '') = '')then
	select	max(cd_estabelecimento)
	into STRICT 	cd_estabelicimento_w
	from	usuario
	where	nm_usuario = nm_usuario_p;

	select 	max(ds_locale)
	into STRICT	tag_pais_w
	from	establishment_locale
	where	cd_estabelecimento = cd_estabelicimento_w;
end if;

return	coalesce(tag_pais_w, 'NoN');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
