CREATE OR REPLACE FUNCTION obtem_status_documento (nr_documento_p ctb_documento.nr_documento%type, nr_seq_doc_compl_p ctb_documento.nr_seq_doc_compl%type, nr_doc_analitico_p ctb_documento.nr_doc_analitico%type, dt_inicial_p timestamp, dt_final_p timestamp, cd_tipo_lote_contabil_p ctb_documento.cd_tipo_lote_contabil%type) RETURNS varchar AS $body$
DECLARE


retorno 			varchar(1);
qt_nao_conciliado_w	integer(15);
qt_conciliado_w		integer(15);
qt_par_conciliado_w	integer(15);


BEGIN

if (coalesce(nr_seq_doc_compl_p, 0) = 0) then
	begin

	select (select count(1)
		from	ctb_documento
		where	nr_documento = nr_documento_p
		and 	dt_competencia between	trunc(dt_inicial_p, 'dd') and fim_dia(dt_final_p)
		and 	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
		and	ie_status 		= 'N'  LIMIT 1)  qt_nao_conciliado,
		(select	count(1)
		from	ctb_documento
		where	nr_documento		= nr_documento_p
		and 	dt_competencia between	trunc(dt_inicial_p, 'dd') and fim_dia(dt_final_p)
		and 	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
		and	ie_status 		= 'C'  LIMIT 1) qt_conciliado,
		(select count(1)
		from	ctb_documento
		where	nr_documento 		= nr_documento_p
		and 	dt_competencia between	trunc(dt_inicial_p, 'dd') and fim_dia(dt_final_p)
		and 	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
		and	ie_status 		= 'P'  LIMIT 1) qt_par_conciliado
	into STRICT	qt_nao_conciliado_w,
		qt_conciliado_w,
		qt_par_conciliado_w
	;

	end;
elsif (coalesce(nr_seq_doc_compl_p, 0) <> 0) and (coalesce(nr_doc_analitico_p, 0) = 0) then
	begin

	select (select count(1)
		from	ctb_documento
		where	nr_documento 		= nr_documento_p
		and	nr_seq_doc_compl 	= nr_seq_doc_compl_p
		and 	dt_competencia between	trunc(dt_inicial_p, 'dd') and fim_dia(dt_final_p)
		and 	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
		and	ie_status 		= 'N'  LIMIT 1)  qt_nao_conciliado,
		(select	count(1)
		from	ctb_documento
		where	nr_documento 		= nr_documento_p
		and	nr_seq_doc_compl 	= nr_seq_doc_compl_p
		and 	dt_competencia between	trunc(dt_inicial_p, 'dd') and fim_dia(dt_final_p)
		and 	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
		and	ie_status 		= 'C'  LIMIT 1) qt_conciliado,
		(select count(1)
		from	ctb_documento
		where	nr_documento 		= nr_documento_p
		and	nr_seq_doc_compl 	= nr_seq_doc_compl_p
		and 	dt_competencia between	trunc(dt_inicial_p, 'dd') and fim_dia(dt_final_p)
		and 	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
		and	ie_status 		= 'P'  LIMIT 1) qt_par_conciliado
	into STRICT	qt_nao_conciliado_w,
		qt_conciliado_w,
		qt_par_conciliado_w
	;

	end;
else
	begin

	select (select count(1)
		from	ctb_documento
		where	nr_documento 		= nr_documento_p
		and	nr_seq_doc_compl 	= nr_seq_doc_compl_p
		and	nr_doc_analitico 	= nr_doc_analitico_p
		and 	dt_competencia between	trunc(dt_inicial_p, 'dd') and fim_dia(dt_final_p)
		and 	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
		and	ie_status 		= 'N'  LIMIT 1)  qt_nao_conciliado,
		(select	count(1)
		from	ctb_documento
		where	nr_documento 		= nr_documento_p
		and	nr_seq_doc_compl 	= nr_seq_doc_compl_p
		and	nr_doc_analitico 	= nr_doc_analitico_p
		and 	dt_competencia between	trunc(dt_inicial_p, 'dd') and fim_dia(dt_final_p)
		and 	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
		and	ie_status 		= 'C'  LIMIT 1) qt_conciliado,
		(select count(1)
		from	ctb_documento
		where	nr_documento 		= nr_documento_p
		and	nr_seq_doc_compl 	= nr_seq_doc_compl_p
		and	nr_doc_analitico 	= nr_doc_analitico_p
		and 	dt_competencia between	trunc(dt_inicial_p, 'dd') and fim_dia(dt_final_p)
		and 	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
		and	ie_status 		= 'P'  LIMIT 1) qt_par_conciliado
	into STRICT	qt_nao_conciliado_w,
		qt_conciliado_w,
		qt_par_conciliado_w
	;

	end;
end if;

if (qt_nao_conciliado_w > 0) and (qt_conciliado_w = 0) and (qt_par_conciliado_w = 0) then
	retorno:= 'N';
elsif (qt_nao_conciliado_w = 0) and (qt_conciliado_w > 0) and (qt_par_conciliado_w = 0) then
	retorno:= 'C';
else
	retorno:= 'P';
end if;

return	retorno;

end;
$body$
