CREATE OR REPLACE FUNCTION obter_abrangencia_plano ( cd_convenio_p bigint, cd_plano_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';
nr_seq_abrangencia_w	bigint;
ds_abrangencia_w	varchar(255);


BEGIN
select	coalesce(max(nr_Seq_abrangencia),null)
into STRICT	nr_seq_abrangencia_w
from	convenio_plano
where	cd_convenio	= cd_convenio_p
and	cd_plano	= cd_plano_p;

if (nr_Seq_abrangencia_w IS NOT NULL AND nr_Seq_abrangencia_w::text <> '') then
	select	coalesce(max(ds_abrangencia),'')
	into STRICT	ds_abrangencia_w
	from	abrangencia_plano
	where	nr_sequencia	= nr_seq_abrangencia_w;
end if;

if (ds_abrangencia_w IS NOT NULL AND ds_abrangencia_w::text <> '') then
	ds_retorno_w	:= ds_abrangencia_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
