CREATE OR REPLACE FUNCTION obter_abrev_estabelecimento ( cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_nome_abrev_w		varchar(50);
cd_cgc_w		varchar(14);

BEGIN

if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	select 	max(cd_cgc)
	into STRICT	cd_cgc_w
	from	estabelecimento
	where	cd_estabelecimento = cd_estabelecimento_p;
end if;

if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	select 	max(ds_nome_abrev)
	into STRICT	ds_nome_abrev_w
	from	pessoa_juridica
	where	cd_cgc = cd_cgc_w;
end if;
return	ds_nome_abrev_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
