CREATE OR REPLACE FUNCTION obter_acao_banco_trans_fin ( nr_seq_trans_financ_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(15);

/*
IE_OPCAO_P
B	= IE_BANCO da Trans financ
BCTB	= Retorna o IE_BANCO da forma cont√°bil
	(D = Pagto, C = Recebimento)

*/
BEGIN

select	coalesce(max(ie_banco),'N')
into STRICT	ie_retorno_w
from	transacao_financeira
where	nr_sequencia	= nr_seq_trans_financ_p;

if (ie_opcao_p = 'BCTB') then

	select	CASE WHEN ie_retorno_w='D' THEN 'R' WHEN ie_retorno_w='C' THEN 'P' END
	into STRICT	ie_retorno_w
	;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
