CREATE OR REPLACE FUNCTION obter_acao_classif_transcr (nr_seq_classif_p bigint) RETURNS varchar AS $body$
DECLARE


ie_acao_transcricao_w	varchar(15);


BEGIN
if (nr_seq_classif_p IS NOT NULL AND nr_seq_classif_p::text <> '') then
	select	coalesce(max(ie_acao_transcricao),'R')
	into STRICT	ie_acao_transcricao_w
	from	classificacao_transcricao
	where	nr_sequencia = nr_seq_classif_p;
else
	ie_acao_transcricao_w	:= 'R';
end if;

return ie_acao_transcricao_w;

end;
$body$
LANGUAGE PLPGSQL
