CREATE OR REPLACE FUNCTION obter_acesso_cad_medico ( cd_empresa_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


cd_perfil_w			bigint;
cd_perfil_ativo_w			bigint;
ie_vinculo_medico_w		bigint;
ie_atualizacao_w			varchar(1);
nm_usuario_lib_w			varchar(15);
qt_registro_w			bigint;
ie_medico_socio_w			varchar(1);
cd_estabelecimento_w			smallint;

c01 CURSOR FOR
SELECT	a.cd_perfil,
	a.nm_usuario_lib,
	a.ie_atualizacao
from	medico_liberacao a
where	a.cd_empresa	= cd_empresa_p
and	coalesce(ie_vinculo_medico, ie_vinculo_medico_w)	= ie_vinculo_medico_w
and	coalesce(cd_perfil, cd_perfil_ativo_w)		= cd_perfil_ativo_w
and	coalesce(nm_usuario_lib, nm_usuario_p)		= nm_usuario_p
and	((coalesce(ie_medico_socio, 'T') = 'T') or (coalesce(ie_medico_socio, 'T') = ie_medico_socio_w))
order by coalesce(a.nm_usuario_lib,'A'),
	coalesce(cd_perfil,0),
	coalesce(ie_medico_socio,'A');


BEGIN

cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
ie_atualizacao_w	:= 'T';

select	count(*)
into STRICT	qt_registro_w
from	medico_liberacao a
where	cd_empresa	= cd_empresa_p;


if (qt_registro_w > 0) then
	begin
	ie_atualizacao_w	:= 'N';
	begin
		cd_perfil_ativo_w	:= coalesce(wheb_usuario_pck.get_cd_perfil,0);
	exception when others then
		cd_perfil_ativo_w	:= 0;
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(278115);
	end;

	select	coalesce(max(ie_vinculo_medico),0)
	into STRICT	ie_vinculo_medico_w
	from	medico
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

	select	coalesce(max(ie_socio),'N')
	into STRICT	ie_medico_socio_w
	from	medico_estabelecimento
	where	cd_pessoa_fisica				= cd_pessoa_fisica_p
	and	coalesce(cd_estabelecimento, cd_estabelecimento_w)	= cd_estabelecimento_w
	and	coalesce(cd_empresa, cd_empresa_p)			= cd_empresa_p;

	open C01;
	loop
	fetch C01 into
		cd_perfil_w,
		nm_usuario_lib_w,
		ie_atualizacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_atualizacao_w	:= ie_atualizacao_w;

		if (coalesce(nm_usuario_lib_w::text, '') = '') and (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then

			select	count(*)
			into STRICT	qt_registro_w
			from	usuario_perfil
			where	cd_perfil	= cd_perfil_w
			and	nm_usuario	= nm_usuario_p;

			if (qt_registro_w = 0) then
				ie_atualizacao_w	:= 'N';
			end if;
		end if;


		end;
	end loop;
	close C01;

	end;
end if;

return	ie_atualizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
