CREATE OR REPLACE FUNCTION obter_acidente_tiss_atend ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_acidente_atend_w		bigint;
ie_retorno_w			bigint;


BEGIN 
 
select	coalesce(nr_seq_tipo_acidente,0) 
into STRICT	cd_acidente_atend_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
select	max(ie_tipo_acidente) 
into STRICT	ie_retorno_w 
from	tiss_tipo_acidente 
where	nr_seq_acidente = cd_acidente_atend_w 
and (coalesce(dt_inicio_vigencia::text, '') = '' or dt_inicio_vigencia <= clock_timestamp());
 
return ie_retorno_w;
 
end;
