CREATE OR REPLACE FUNCTION obter_acomod_perfil_regra (cd_perfil_p bigint) RETURNS bigint AS $body$
DECLARE


cd_tipo_acomodacao_w 		smallint;
cd_estabelecimento_w		integer;


BEGIN

cd_estabelecimento_w	:=	wheb_usuario_pck.get_cd_estabelecimento;

select	max(cd_tipo_acomodacao)
into STRICT	cd_tipo_acomodacao_w
from	regra_tipo_acomodacao
where	cd_perfil	= cd_perfil_p
and	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w;

return	cd_tipo_acomodacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
