CREATE OR REPLACE FUNCTION obter_acomp_atendimento ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_acompanhante_w			varchar(255);
nm_acompanhante_w			varchar(40);
ds_separador_w			varchar(02);

c01 CURSOR FOR 
	SELECT	coalesce(substr(obter_nome_pf(cd_pessoa_fisica),1,40),nm_acompanhante) 
	from	atendimento_acompanhante 
	where	nr_atendimento	= nr_atendimento_p 
	and	((cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '') or (nm_acompanhante IS NOT NULL AND nm_acompanhante::text <> '')) 
	and	coalesce(dt_saida::text, '') = '';


BEGIN 
 
ds_acompanhante_w			:= '';
ds_separador_w			:= '';
 
OPEN C01;
LOOP 
FETCH C01 into	 
		nm_acompanhante_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (coalesce(length(ds_acompanhante_w),0) < 180) then 
		ds_acompanhante_w	:= ds_acompanhante_w || ds_separador_w || nm_acompanhante_w;
	end if;
	ds_separador_w		:= ', ';
END LOOP;
CLOSE C01;
 
RETURN ds_acompanhante_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_acomp_atendimento ( nr_atendimento_p bigint) FROM PUBLIC;

