CREATE OR REPLACE FUNCTION obter_adep_processo_frac_aux ( nr_ordem_p adep_processo_frac_aux.nr_seq_ordem%type, cd_material_p adep_processo_frac_aux.cd_material%type, campo_retorno_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(50);
qt_sobra_w					adep_processo_frac_aux.qt_sobra%type;
cd_um_sobra_w				adep_processo_frac_aux.cd_um_sobra%type;
nm_usuario_conf_w			adep_processo_frac_aux.nm_usuario_conf%type;
nm_usuario_conf_aux_w		adep_processo_frac_aux.nm_usuario_conf_aux%type;
qt_sobra_c					constant varchar(3) := 'QTS';
cd_um_sobra_c				constant varchar(3) := 'CDS';
nm_usuario_conf_c			constant varchar(3) := 'NMC';
nm_usuario_conf_aux_c		constant varchar(3) := 'NMA';


BEGIN

if (nr_ordem_p IS NOT NULL AND nr_ordem_p::text <> '' AND cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select max(qt_sobra),
		max(cd_um_sobra),
		max(nm_usuario_conf),
		max(nm_usuario_conf_aux)
	into STRICT
		qt_sobra_w,
		cd_um_sobra_w,
		nm_usuario_conf_w,
		nm_usuario_conf_aux_w
	from adep_processo_frac_aux
	where nr_seq_ordem = nr_ordem_p
	and	cd_material = cd_material_p;

	if (campo_retorno_p = qt_sobra_c) then
		ds_retorno_w := qt_sobra_w;

	elsif (campo_retorno_p = cd_um_sobra_c) then
		ds_retorno_w := cd_um_sobra_w;
		
	elsif (campo_retorno_p = nm_usuario_conf_c) then
		ds_retorno_w := nm_usuario_conf_w;

	elsif (campo_retorno_p = nm_usuario_conf_aux_c) then
		ds_retorno_w := nm_usuario_conf_aux_w;
	else ds_retorno_w := null;
	end if;
end if;

return ds_retorno_w;

end;
