CREATE OR REPLACE FUNCTION obter_adiantamentos_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_adiantamento_w	bigint;
vl_adiantamento_w	double precision;
ds_lista_adiant_w	varchar(4000);

c01 CURSOR FOR
SELECT	a.nr_adiantamento,
	a.vl_adiantamento
from	adiantamento a
where	coalesce(a.vl_saldo,0)	<> 0
and	a.nr_atendimento	= nr_atendimento_p
order by	1;


BEGIN

open	c01;
loop
fetch	c01 into
	nr_adiantamento_w,
	vl_adiantamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (coalesce(ds_lista_adiant_w::text, '') = '') or (length(ds_lista_adiant_w) < 3990) then
		if (coalesce(ds_lista_adiant_w::text, '') = '') then
			ds_lista_adiant_w	:= nr_adiantamento_w || ' (' || trim(both to_char(vl_adiantamento_w,'999,999,999.99')) || ')';
		else
			ds_lista_adiant_w	:= ds_lista_adiant_w || ', ' || nr_adiantamento_w || ' (' || trim(both to_char(vl_adiantamento_w,'999,999,999.99')) || ')';
		end if;
	end if;

end	loop;
close	c01;

RETURN ds_lista_adiant_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
