CREATE OR REPLACE FUNCTION obter_adiantamentos_pf_pj ( cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


nr_adiantamento_w	bigint;
ds_lista_adiant_w	varchar(4000);

c01 CURSOR FOR
SELECT	a.nr_adiantamento
from	adiantamento a
where	coalesce(a.vl_saldo,0)	<> 0
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p

union

select	a.nr_adiantamento
from	adiantamento a
where	coalesce(a.vl_saldo,0)	<> 0
and	a.cd_cgc		= cd_cgc_p
order by	1;


BEGIN

open	c01;
loop
fetch	c01 into
	nr_adiantamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (length(ds_lista_adiant_w) < 3990) then
		if (coalesce(ds_lista_adiant_w::text, '') = '') then
			ds_lista_adiant_w	:= nr_adiantamento_w;
		else
			ds_lista_adiant_w	:= ds_lista_adiant_w || ', ' || nr_adiantamento_w;
		end if;
	end if;

end	loop;
close	c01;

RETURN ds_lista_adiant_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
