CREATE OR REPLACE FUNCTION obter_adiantamento_aberto_pf ( cd_pessoa_fisica_p text, vl_despesa_p bigint, nr_titulo_p bigint, ie_opcao_p text, nr_seq_gv_p bigint default 0) RETURNS varchar AS $body$
DECLARE


vl_adiantamento_w		double precision;
vl_pagar_w		double precision;

/* Opções:
VA : valor do adiantamento
VP:  valor a pagar (despesa de viagem - valor do adiantamento)
*/
BEGIN

IF (coalesce(nr_titulo_p::text, '') = '') THEN

	SELECT	SUM(vl_saldo)
	INTO STRICT	vl_adiantamento_w
	FROM	adiantamento_pago
	WHERE	cd_pessoa_fisica = cd_pessoa_fisica_p
	AND	((nr_seq_gv_p = 0) OR (nr_seq_gv_p <> 0 AND nr_seq_gv = nr_seq_gv_p))
	AND	coalesce(dt_baixa::text, '') = ''
	AND	vl_saldo > 0;

ELSE

	SELECT	SUM(vl_adiantamento)
	INTO STRICT	vl_adiantamento_w
	FROM	titulo_pagar_adiant
	WHERE	nr_titulo = nr_titulo_p;

END IF;

vl_pagar_w := vl_despesa_p - vl_adiantamento_w;

IF (ie_opcao_p = 'VA') THEN
	RETURN	vl_adiantamento_w;
ELSIF (ie_opcao_p = 'VP') THEN
	RETURN vl_pagar_w;
END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
