CREATE OR REPLACE FUNCTION obter_adiantamento_pago (cd_pessoa_fisica_p text, cd_cgc_p text, cd_estabelecimento_p bigint, dt_limite_p timestamp default clock_timestamp()) RETURNS bigint AS $body$
DECLARE


vl_saldo_adiant_w	adiantamento_pago.vl_saldo%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select (coalesce(sum(a.vl_saldo),0))
	into STRICT vl_saldo_adiant_w
	from	adiantamento_pago a
	where	a.cd_estabelecimento 	= cd_estabelecimento_p
	and	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and (trunc(coalesce(a.dt_baixa,a.dt_adiantamento), 'dd') <= trunc(dt_limite_p, 'dd') or coalesce(dt_limite_p::text, '') = '');

else

	select (coalesce(sum(a.vl_saldo),0))
	into STRICT vl_saldo_adiant_w
	from	adiantamento_pago a
	where	a.cd_estabelecimento 	= cd_estabelecimento_p
	and	a.cd_cgc 		= cd_cgc_p
	and (trunc(coalesce(a.dt_baixa,a.dt_adiantamento), 'dd') <= trunc(dt_limite_p, 'dd') or coalesce(dt_limite_p::text, '') = '');

end if;

return	vl_saldo_adiant_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
