CREATE OR REPLACE FUNCTION obter_adiant_desp_viagem ( cd_pessoa_fisica_p text ) RETURNS bigint AS $body$
DECLARE


vl_saldo_w		double precision := 0;


BEGIN

select	coalesce(sum(coalesce(vl_saldo, 0)), 0)
into STRICT	vl_saldo_w
from	adiantamento_pago
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	coalesce(dt_baixa::text, '') = ''
and	vl_saldo > 0;

return	vl_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
