CREATE OR REPLACE FUNCTION obter_adiant_gv_vinculada (nr_seq_fech_proj_p bigint) RETURNS bigint AS $body$
DECLARE


valor_adiantamento_w	double precision := 0;
vl_adiantamento_w 	double precision := 0;
nr_seq_viagem_w		bigint;

c01 CURSOR FOR
	SELECT	nr_seq_viagem
	from	via_relat_desp
	where	nr_seq_fech_proj = nr_seq_fech_proj_p;


BEGIN
	open c01;
	loop
	fetch c01 into
		nr_seq_viagem_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			select	coalesce(sum(coalesce(vl_saldo, 0)), 0)
			into STRICT	vl_adiantamento_w
			from	adiantamento_pago
			where	((nr_seq_viagem_w = 0) or (nr_seq_viagem_w <> 0 AND nr_seq_gv = nr_seq_viagem_w))
			and	coalesce(dt_baixa::text, '') = ''
			and	vl_saldo > 0;
			valor_adiantamento_w := valor_adiantamento_w + vl_adiantamento_w;
		end;
	end loop;
	close c01;

	return	valor_adiantamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
