CREATE OR REPLACE FUNCTION obter_administracao_intervalo (cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


param605_w varchar(1);
param809_w varchar(1);
ie_acm_w   varchar(1);
ie_se_necessario_w varchar(1);
ie_administracao_w varchar(1) := 'P';


BEGIN
	select coalesce(max(ie_acm), 'N'),
		   coalesce(max(ie_se_necessario), 'N')
	into STRICT
		ie_acm_w,
		ie_se_necessario_w
	from intervalo_prescricao
	where cd_intervalo = cd_intervalo_p;
	
	param809_w := obter_param_usuario(924, 809, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, param809_w);
	param605_w := obter_param_usuario(924, 605, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, param605_w);
	
	if (ie_se_necessario_w = 'S' and coalesce(param605_w, 'N') = 'S' and coalesce(param809_w, 'N') = 'S') then
		ie_administracao_w := 'N';
	elsif (ie_acm_w = 'S' and coalesce(param809_w,'N') = 'S') then
		ie_administracao_w := 'C';
	end if;
        
	return ie_administracao_w;	
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
