SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_agendamento_dia (cd_estabelecimento_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE

 
dt_agenda_w		agenda_consulta.dt_agenda%type;
ds_retorno_w		varchar(60);

 

BEGIN 
 
select 	max(dt_agenda) 
into STRICT	dt_agenda_w 
from 	agenda a, 
	agenda_consulta b, 
	agenda_classif c 
where 	a.cd_agenda = b.cd_agenda 
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p 
and 	b.ie_status_agenda not in ('C', 'F', 'I', 'S') 
and 	b.dt_agenda between pkg_date_utils.START_OF( dt_agenda_p , 'DD') and pkg_date_utils.END_OF( dt_agenda_p , 'DAY') 
and	b.IE_CLASSIF_AGENDA = c.CD_CLASSIFICACAO 
and	c.ie_validar_poltrona = 'S';
 
if (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then 
	ds_retorno_w := to_char( dt_agenda_w , pkg_date_formaters.localize_mask('timestamp', pkg_date_formaters.getUserLanguageTag( cd_estabelecimento_p, nm_usuario_p )));
else 
	ds_retorno_w := Wheb_mensagem_pck.get_texto(887407);
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
