CREATE OR REPLACE FUNCTION obter_agendas_agrupamento ( nr_seq_agrupamento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_agendas_w	varchar(1000);
cd_agenda_w	bigint;
					
C01 CURSOR FOR 
	SELECT	cd_agenda 
	from	agenda 
	where	nr_seq_agrupamento = nr_seq_agrupamento_p;
					

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	cd_agenda_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (coalesce(ds_agendas_w::text, '') = '') then 
		ds_agendas_w	:= to_char(cd_agenda_w);
	else 
		ds_agendas_w	:= ds_agendas_w || ',' || to_char(cd_agenda_w);
	end if;
	 
	end;
end loop;
close C01;
 
return	ds_agendas_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
