CREATE OR REPLACE FUNCTION obter_agenda_cons_lista_espera (cd_pessoa_fisica_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

			 
cd_agenda_w		bigint;
ds_agenda_w		varchar(255);
ds_nome_agenda_w	varchar(255);
ds_retorno_w		varchar(255);


BEGIN 
 
select 	coalesce(max(a.cd_agenda),0), 
	max(substr(obter_nome_medico(b.cd_pessoa_fisica,'N'),1,200)), 
	coalesce(max(b.ds_agenda),'') 
into STRICT	cd_agenda_w, 
	ds_agenda_w, 
	ds_nome_agenda_w 
from 	agenda_lista_espera a, 
	agenda b 
where	a.cd_agenda = b.cd_agenda 
and	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
and 	ie_status_espera = 'A' 
and	coalesce(b.ie_lista_espera,'N') = 'N';
 
if (cd_agenda_w > 0) then 
 
	if (ie_opcao_p = 0) then 
		ds_retorno_w	:= ds_agenda_w;
	elsif (ie_opcao_p = 1) then 
		ds_retorno_w	:= ds_nome_agenda_w;
	end if;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_agenda_cons_lista_espera (cd_pessoa_fisica_p text, ie_opcao_p bigint) FROM PUBLIC;

