CREATE OR REPLACE FUNCTION obter_agenda_regra_permissao ( cd_pessoa_fisica_p text, cd_agenda_p bigint, ie_permite_tipo_p text, cd_perfil_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'S';
cd_perfil_w	bigint;
ie_permite_w		varchar(1);
ie_permite_lib_w	varchar(1);

C01 CURSOR FOR
	SELECT CASE WHEN ie_permite_tipo_p='C' THEN coalesce(ie_permite_cancelar_agenda,'S') WHEN ie_permite_tipo_p='E' THEN coalesce(ie_permite_encaixe_agenda,'S') WHEN ie_permite_tipo_p='VI' THEN coalesce(ie_permite_vincular_atend,'N') WHEN ie_permite_tipo_p='ST' THEN coalesce(ie_permite_status,'N') WHEN ie_permite_tipo_p='CO' THEN coalesce(ie_permite_alterar_agenda,'S') WHEN ie_permite_tipo_p='CE' THEN coalesce(ie_permite_confirmar_encaixe,'S') WHEN ie_permite_tipo_p='VA' THEN coalesce(ie_permite_visualizar,'S') END
	from	agenda_regra_permissao
	where 	cd_agenda = cd_agenda_p
	and	coalesce(cd_pessoa_fisica,cd_pessoa_fisica_p) = cd_pessoa_fisica_p
	and 	ie_situacao = 'A'
	and	coalesce(cd_perfil, cd_perfil_w) = cd_perfil_w
	order by cd_pessoa_fisica desc;


BEGIN

if (coalesce(cd_perfil_p::text, '') = '') then
	cd_perfil_w := 0;
else
	cd_perfil_w := cd_perfil_p;
end if;


open C01;
loop
fetch C01 into
	ie_permite_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ie_retorno_w := ie_permite_w;
	end;
end loop;
close C01;

if (ie_permite_tipo_p = 'CO') and (cd_perfil_p > 0) and (coalesce(ie_permite_w,'S') = 'S') then

	select 	max(ie_permite_alterar)
	into STRICT	ie_permite_lib_w
	from	AGENDA_REGRA_LIB_PERFIL
	where 	cd_agenda = cd_agenda_p
	and	coalesce(cd_perfil, cd_perfil_w) = cd_perfil_w;

	if (ie_permite_lib_w = 'N') then
		ie_retorno_w	:= ie_permite_lib_w;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
