CREATE OR REPLACE FUNCTION obter_agenda_turno_medico (cd_agenda_p text) RETURNS varchar AS $body$
DECLARE

 
ds_agenda_w		varchar(255) := '';


BEGIN 
 
select	max(substr(ds_agenda,1,255)) 
into STRICT	ds_agenda_w 
from	agenda 
where 	ie_situacao = 'A' 
and 	ie_agenda_integrada = 'S' 
and 	cd_tipo_agenda = 2 
and	cd_agenda = cd_agenda_p;
 
if (coalesce(ds_agenda_w::text, '') = '') then 
 
	select	substr(obter_nome_medico_combo_agcons(cd_estabelecimento, cd_agenda, cd_tipo_agenda, CASE WHEN coalesce(ie_ordenacao::text, '') = '' THEN  'N'  ELSE ie_ordenacao END ), 1, 240) 
	into STRICT	ds_agenda_w 
	from 	agenda 
	where 	ie_situacao = 'A' 
	and 	ie_agenda_integrada = 'S' 
	and 	cd_tipo_agenda in (3,4) 
	and	cd_agenda = cd_agenda_p;
	 
end if;
 
return ds_agenda_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
