CREATE OR REPLACE FUNCTION obter_agend_prescr_proc ( nr_seq_interno_p bigint) RETURNS bigint AS $body$
DECLARE
			
			
ds_retorno_w		bigint;
nr_seq_agenda_w		prescr_medica.nr_seq_agenda%type;


BEGIN

select	max(coalesce(b.nr_seq_agenda,a.nr_seq_agenda))
into STRICT	nr_seq_agenda_w
from	prescr_procedimento a,
	prescr_medica b
where	a.nr_prescricao = b.nr_prescricao
and	a.nr_seq_interno = nr_seq_interno_p;


select	coalesce(max(b.cd_agenda),0)
into STRICT	ds_retorno_w
from	agenda_paciente b
where	b.nr_sequencia = nr_seq_agenda_w;

return	ds_retorno_w;

end;
