CREATE OR REPLACE FUNCTION obter_agend_quimio_bloq_prof ( dt_agendamento_p timestamp, hr_inicial_dia_p timestamp, hr_final_dia_p timestamp, nr_seq_prof_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
ie_dia_semana_w		integer;


BEGIN

if (nr_seq_prof_p IS NOT NULL AND nr_seq_prof_p::text <> '') then

	select 	obter_cod_dia_semana(dt_agendamento_p)
	into STRICT	ie_dia_semana_w
	;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	qt_prof_bloqueio
	where	nr_seq_prof	= nr_seq_prof_p
	and	trunc(dt_agendamento_p) between dt_inicial and dt_final
	and	((coalesce(ie_dia_semana, ie_dia_semana_w) = ie_dia_semana_w) or ((ie_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))))
	and	(hr_inicial_bloqueio - trunc(hr_inicial_bloqueio))::numeric  <= (hr_inicial_dia_p - trunc(hr_inicial_dia_p))::numeric 
	and	(hr_final_bloqueio  - trunc(hr_final_bloqueio))::numeric   >= (hr_final_dia_p - trunc(hr_final_dia_p))::numeric;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
