CREATE OR REPLACE FUNCTION obter_agend_quimio_bloq ( dt_agendamento_p timestamp, hr_inicial_dia_p timestamp, hr_final_dia_p timestamp, nr_seq_local_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_horario_bloqueado_w		varchar(1) := 'N';
ie_dia_semana_w			varchar(255);
dt_agendamento_w		timestamp;


BEGIN

dt_agendamento_w := pkg_date_utils.start_of(dt_agendamento_p,'DD');


select	max(coalesce('S','N'))
into STRICT	ie_horario_bloqueado_w
from	qt_bloqueio
where	nr_seq_local 	= nr_seq_local_p
and	dt_agendamento_w between trunc(dt_inicial)  and trunc(dt_final);

if (ie_horario_bloqueado_w = 'S') then

	ie_dia_semana_w	:= obter_cod_dia_semana(dt_agendamento_w);

	select	max(coalesce('S','N'))
	into STRICT	ie_horario_bloqueado_w
	from	qt_bloqueio
	where	nr_seq_local 	= nr_seq_local_p
	and	dt_agendamento_w between trunc(dt_inicial)  and trunc(dt_final)
	and ((hr_inicio_bloqueio - trunc(hr_inicio_bloqueio))::numeric  <= (hr_inicial_dia_p - trunc(hr_inicial_dia_p))::numeric  or coalesce(hr_inicio_bloqueio::text, '') = '')
	and ((hr_final_bloqueio  - trunc(hr_final_bloqueio))::numeric   >= (hr_final_dia_p - trunc(hr_final_dia_p))::numeric  or coalesce(hr_final_bloqueio::text, '') = '')
	and	(((dt_dia_semana = ie_dia_semana_w) or ((dt_dia_semana = 9) and (ie_dia_Semana_w not in (7,1)))) or coalesce(dt_dia_semana::text, '') = '')
	and	not exists	(SELECT 1
					from agenda_quimio x
					where x.nr_seq_local = nr_seq_local_p
					and x.nr_seq_local = nr_seq_local
					and hr_inicial_dia_p between dt_agenda and dt_agenda + (x.nr_minuto_duracao-1)/1440
					and ie_status_agenda not in ('C','F','S'));
end if;

return	ie_horario_bloqueado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
