CREATE OR REPLACE FUNCTION obter_agrupamento_atendimento (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_agrupamento_w	bigint := null;
cd_setor_atendimento_w	bigint;


BEGIN

select	max(cd_setor_atendimento)
into STRICT	cd_setor_atendimento_w
from	atend_paciente_unidade a
where	nr_atendimento = nr_atendimento_p
and	coalesce(a.dt_saida_unidade, a.dt_entrada_unidade + 9999)	=
		(SELECT max(coalesce(b.dt_saida_unidade, b.dt_entrada_unidade + 9999))
		from 	atend_paciente_unidade b
		where 	b.nr_atendimento = nr_atendimento_p);

if (cd_setor_atendimento_w IS NOT NULL AND cd_setor_atendimento_w::text <> '') then
	select	max(nr_seq_agrupamento)
	into STRICT	nr_seq_agrupamento_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_w;
end if;

return	coalesce(nr_seq_agrupamento_w, 0);

end;
