CREATE OR REPLACE FUNCTION obter_agrup_prot_medic_mat ( cd_protocolo_p bigint, nr_sequencia_p bigint, ie_agrupador_p bigint) RETURNS bigint AS $body$
DECLARE


nr_agrupamento_w	bigint;


BEGIN
if (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin

	if (coalesce(ie_agrupador_p::text, '') = '') or (ie_agrupador_p = 0) then
		begin

		select (coalesce(max(nr_agrupamento),0) + 1)
		into STRICT	nr_agrupamento_w
		from	protocolo_medic_material
		where	cd_protocolo = cd_protocolo_p
		and	nr_sequencia = nr_sequencia_p
		and	ie_agrupador < 3;
		end;
	else
		begin
		select (coalesce(max(nr_agrupamento),0) + 1)
		into STRICT	nr_agrupamento_w
		from	protocolo_medic_material
		where	cd_protocolo = cd_protocolo_p
		and	nr_sequencia = nr_sequencia_p
		and	ie_agrupador = ie_agrupador_p;
		end;
	end if;
	end;
end if;

return nr_agrupamento_w;
end;
$body$
