CREATE OR REPLACE FUNCTION obter_agrup_setor ( cd_setor_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_agrupamento_w 	varchar(255);
nr_seq_agrupamento_w	bigint;


BEGIN

/*Alterado por que estava com a integriadade errada.
Geliard*/
select	max(nr_seq_agrupamento)
into STRICT	nr_seq_agrupamento_w
from	setor_atendimento
where	cd_setor_atendimento	= cd_setor_atendimento_p;

if (ie_opcao_p	= 'D') then

	select  coalesce(max(ds_agrupamento),WHEB_MENSAGEM_PCK.get_texto(312748)) --Sem agrupamento
	into STRICT	ds_agrupamento_w
	from	agrupamento_setor b
	where   b.nr_sequencia = nr_seq_agrupamento_w;
else
	ds_agrupamento_w	:= nr_seq_agrupamento_w;
end if;

return	ds_agrupamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
