SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_alergias ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_alergia_w 	varchar(4000);
ds_retorno_w	varchar(4000) := null;
ds_agente_w		varchar(4000) := null;
ie_liberar_hist_saude_w		varchar(1);
ie_classificacao_alergia_w		varchar(1);

C01 CURSOR FOR
	SELECT	distinct coalesce(ds_ficha_tecnica,ds_dcb,ds_dcb_mat,ds_material,ds_classe_mat,ds_medic_nao_cad,ds_alergeno,ds_familia_mat) ds_agente, ie_classificacao
	from	alergia_v
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	((ie_liberar_hist_saude_w = 'N') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''))
	order	by 1;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
   select	coalesce(max(ie_liberar_hist_saude),'N')
   into STRICT	ie_liberar_hist_saude_w
   from	parametro_medico
   where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

   open c01;
   loop
   fetch	c01 into
      ds_alergia_w,
	  ie_classificacao_alergia_w;
   EXIT WHEN NOT FOUND; /* apply on c01 */
      if (ds_alergia_w IS NOT NULL AND ds_alergia_w::text <> '')then
      ds_retorno_w := ds_retorno_w || ds_alergia_w || (case when ie_classificacao_alergia_w =  'I' then ' (' || obter_desc_expressao(1068383) || ')' else ' ' end) || ', ';
      end if;
   end loop;
   close C01;

   if (substr((ds_retorno_w),1,length(ds_retorno_w)-2) is not null) then
      ds_retorno_w := substr((ds_retorno_w),1,length(ds_retorno_w)-2) || '.';
   else
      ds_retorno_w := substr((ds_retorno_w),1,length(ds_retorno_w)-2);	
   end if;
end if;

return	ds_retorno_w;

--tratamento em pontuacao final da string e usar separador ' , ' apenas se houver informacao.
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
