CREATE OR REPLACE FUNCTION obter_alergia_contraste_heg (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
qt_alergia_w	bigint;
ds_retorno_w	varchar(255);


BEGIN 
 
select	count(*) 
into STRICT	qt_alergia_w 
from	paciente_alergia 
where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	nr_seq_tipo		= 1;
 
if (qt_alergia_w = 0) then 
	ds_retorno_w	:= '[ ] Sim   [ ] N達o   [X] N達o sabe';
else 
	ds_retorno_w	:= '[X] Sim   [ ] N達o   [ ] N達o sabe';
end if;
 
RETURN	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
