SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_alertas_mapa_setor (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

				 
ds_alerta_w	varchar(2000);
dt_alerta_w	timestamp;
ds_retorno_w	varchar(10000);

c01 CURSOR FOR 
 
SELECT	a.dt_alerta, 
	a.ds_alerta 
FROM	atendimento_alerta a, 
	atendimento_paciente b 
WHERE	b.cd_pessoa_fisica = cd_pessoa_fisica_p 
AND		a.nr_atendimento = b.nr_atendimento 
AND	a.ie_situacao = 'A' 
and	((coalesce(a.dt_fim_alerta::text, '') = '') or (a.dt_fim_alerta >= trunc(clock_timestamp(),'dd'))) 
and	obter_se_tipo_alerta_lib(a.nr_seq_tipo_alerta,wheb_usuario_pck.get_nm_usuario)	= 'S' 

UNION ALL
 
SELECT	a.dt_alerta, 
	a.ds_alerta 
FROM	Alerta_paciente a 
WHERE	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
AND	a.ie_situacao = 'A' 
and	((coalesce(a.dt_fim_alerta::text, '') = '') or (a.dt_fim_alerta >= trunc(clock_timestamp(),'dd'))) 
and	obter_se_tipo_alerta_lib(a.nr_seq_tipo_alerta,wheb_usuario_pck.get_nm_usuario)	= 'S' 
ORDER BY 1, 2;


BEGIN 
 
ds_retorno_w := '';
 
open c01;
loop 
fetch 	c01 into 
  	dt_alerta_w, 
	ds_alerta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w := ds_retorno_w ||'<br>'|| to_char(dt_alerta_w, 'dd/mm/yyyy hh24:mi:ss') || ' - ' || ds_alerta_w;
end loop;
close c01;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
