CREATE OR REPLACE FUNCTION obter_alerta_mat_rot (nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_material_p bigint, cd_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_material_w			bigint;
cd_unidade_medida_w		varchar(30);
cd_Setor_atendimento_w	integer;
qt_idade_w				bigint;
ie_via_aplicacao_w		varchar(30);
ds_mensagem_w			varchar(2000);
cd_convenio_w			integer;

C01 CURSOR FOR
	SELECT	ds_mensagem
	from	regra_alerta_material
	where	ie_situacao	= 'A'
	and 	coalesce(ie_atend_prescr,'N') = 'S'
	and		coalesce(cd_Setor_atendimento,cd_Setor_atendimento_w)	= cd_Setor_atendimento_w
	and		coalesce(cd_material,cd_material_w)				= cd_material_w
	and		coalesce(cd_unidade_medida,cd_unidade_medida_w)		= cd_unidade_medida_w
	and		coalesce(ie_via_aplicacao,ie_via_aplicacao_w)		= ie_via_aplicacao_w
	and		coalesce(cd_convenio, cd_convenio_w)				= cd_convenio_w
	and		((coalesce(cd_perfil::text, '') = '') or (cd_perfil = obter_perfil_ativo))
	and		qt_idade_w 		between coalesce(qt_idade_min, 0) and coalesce(qt_idade_max, 999)
	and		((coalesce(nr_seq_estrutura::text, '') = '')	or (consistir_se_mat_estrutura(nr_seq_estrutura,cd_material_w)	= 'S'))
	order by	coalesce(qt_idade_min, 0),
				coalesce(cd_material,0),
				coalesce(nr_seq_estrutura,0),
				coalesce(cd_Setor_atendimento,0),
				coalesce(ie_via_aplicacao,'0'),
				coalesce(cd_unidade_medida,'0');


BEGIN
begin

select	coalesce(a.cd_setor_atendimento,0),
		coalesce(obter_idade_pf(a.cd_pessoa_fisica,clock_timestamp(),'A'),0),
		coalesce(obter_convenio_atendimento(a.nr_atendimento),0)
into STRICT	cd_Setor_atendimento_w,
		qt_idade_w,
		cd_convenio_w
from	prescr_medica a
where	a.nr_prescricao		= nr_prescricao_p;

select	a.cd_material,
		coalesce(a.ie_via_aplicacao,'0'),
		coalesce(a.cd_unidade_medida,'0')
into STRICT	cd_material_w,
		ie_via_aplicacao_w,
		cd_unidade_medida_w
from	protocolo_medic_material a
where	cd_protocolo 	= cd_protocolo_p
and		nr_sequencia 	= nr_sequencia_p
and		nr_seq_material = nr_seq_material_p;


open C01;
loop
fetch C01 into
	ds_mensagem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

exception
	when others then
	null;
	end;

return ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_alerta_mat_rot (nr_prescricao_p bigint, nr_sequencia_p bigint, nr_seq_material_p bigint, cd_protocolo_p bigint) FROM PUBLIC;

