CREATE OR REPLACE FUNCTION obter_alerta_mews_sv (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w	bigint;


BEGIN
select	count(*)
into STRICT	qt_reg_w
from	atendimento_sinal_vital
where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and		coalesce(dt_inativacao::text, '') = ''
and		nr_atendimento = nr_atendimento_p
and		trunc(dt_liberacao) = trunc(clock_timestamp())
and 	(QT_FREQ_CARDIACA IS NOT NULL AND QT_FREQ_CARDIACA::text <> '')
and 	(QT_FREQ_RESP IS NOT NULL AND QT_FREQ_RESP::text <> '')
and 	(QT_TEMP IS NOT NULL AND QT_TEMP::text <> '')
and 	(QT_PAM IS NOT NULL AND QT_PAM::text <> '')
and 	(IE_NIVEL_CONSCIENCIA IS NOT NULL AND IE_NIVEL_CONSCIENCIA::text <> '');

if (qt_reg_w > 0) then
	return 'S';
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
