CREATE OR REPLACE FUNCTION obter_alerta_nutricional ( cd_pessoa_fisica_p text, dt_dieta_p timestamp) RETURNS varchar AS $body$
DECLARE

ds_alerta_w	varchar(255);
quebra_w	varchar(10) := chr(13)||chr(10);
c01 CURSOR FOR
	SELECT	ds_alerta
	from	nut_alerta
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and (coalesce(dt_dieta_p,clock_timestamp()) between trunc(coalesce(dt_inicio_vigencia,coalesce(dt_dieta_p,clock_timestamp()))) and fim_dia(coalesce(dt_fim_vigencia,coalesce(dt_dieta_p,clock_timestamp()))));

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into ds_alerta_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		if (ds_alerta_w IS NOT NULL AND ds_alerta_w::text <> '') then
			begin
			ds_alerta_w	:=	substr(ds_alerta_w || quebra_w,1,255);
			end;
		end if;
	end loop;
	close c01;
	end;
end if;
return	ds_alerta_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
