CREATE OR REPLACE FUNCTION obter_alerta_projeto_usuario ( nr_seq_projeto_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

 
cd_pessoa_usuario_w	varchar(10);
ie_alerta_w		varchar(15) := null;
		

BEGIN 
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	cd_pessoa_usuario_w := obter_pessoa_fisica_usuario(nm_usuario_p,'C');
	if (cd_pessoa_usuario_w IS NOT NULL AND cd_pessoa_usuario_w::text <> '') then 
		begin 
		select	max(ie_alerta) 
		into STRICT	ie_alerta_w 
		from	proj_alerta 
		where	nr_seq_projeto = nr_seq_projeto_p 
		and	cd_pessoa_destino_alerta = cd_pessoa_usuario_w 
		and	coalesce(dt_ciente::text, '') = '';
		end;
	end if;
	end;
end if;
return ie_alerta_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
