CREATE OR REPLACE FUNCTION obter_alias_consulta_generico (ds_sql_p text, separator_p text) RETURNS varchar AS $body$
DECLARE

/*
ds_sql_p: devera ser uma consulta normal, algo como 
          'select a.*, b.* from tabela A, tabela B where...'
          'select xx1.primeiro_atributo, ww.*, xx1.ultimo_atributo from primeira_tabela ww.... ultima_tabela as xx1'

separator_p: nao eh obrigatorio
*/
ds_retorno_w          varchar(4000) := null;
separator_w           varchar(10) := ';';

l_cursor      bigint := dbms_sql.open_cursor;
l_desc        dbms_sql.desc_tab2;
l_cnt         bigint;

BEGIN
  if (coalesce(ds_sql_p::text, '') = '' or length(ds_sql_p) = 0) then
    return ds_retorno_w;
  end if;

  if ((separator_p IS NOT NULL AND separator_p::text <> '') and length(separator_p) > 0) then
    separator_w := separator_p;
  end if;

  dbms_sql.parse(l_cursor, ds_sql_p, dbms_sql.native);
  dbms_sql.describe_columns2( l_cursor, l_cnt, l_desc );
  FOR i IN 1 .. l_cnt
  LOOP
    ds_retorno_w := ds_retorno_w || l_desc[i].col_name || separator_w;
  END LOOP;
  dbms_sql.close_cursor( l_cursor );

  return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
