CREATE OR REPLACE FUNCTION obter_alias_estrut_conta (cd_convenio_p bigint, nm_atributo_p text, cd_estrutura_p bigint) RETURNS varchar AS $body$
DECLARE


nm_alias_w	varchar(200);


BEGIN

select 	max(a.NM_ALIAS)
into STRICT	nm_alias_w
from   	convenio_estrutura_conta b,
	convenio_estrut_atrib a
where  	a.nr_sequencia = b.nr_sequencia
and    	b.cd_estrutura = cd_estrutura_p
and    	b.cd_convenio  = cd_convenio_p
and    	a.nm_atributo  = nm_atributo_p;

if (coalesce(nm_alias_w::text, '') = '') then
	select 	max(a.NM_ALIAS)
	into STRICT	nm_alias_w
	from   	convenio_estrutura_conta b,
		convenio_estrut_atrib a
	where  	a.nr_sequencia = b.nr_sequencia
	and    	b.cd_estrutura = cd_estrutura_p
	and	coalesce(b.cd_convenio::text, '') = ''
	and    	a.nm_atributo  = nm_atributo_p;
end if;

return	nm_alias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
