CREATE OR REPLACE FUNCTION obter_alta_tiss_atend ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_motivo_alta_atend_w		bigint;
ie_retorno_w				bigint;


BEGIN 
 
select	coalesce(cd_motivo_alta,0) 
into STRICT	cd_motivo_alta_atend_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
select	max(nr_seq_saida_spsadt) 
into STRICT	ie_retorno_w 
from	tiss_motivo_alta 
where	cd_motivo_alta = cd_motivo_alta_atend_w 
and (coalesce(dt_inicio_vigencia::text, '') = '' or dt_inicio_vigencia <= clock_timestamp());
 
return ie_retorno_w;
 
end;
