SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_alta_tiss_conta ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_saida_w				bigint;
ie_retorno_w				bigint;

/*
C - Consulta
I - Internação
S - Saída SP/SADT
*/
BEGIN


if (ie_opcao_p = 'C') then

	select	coalesce(nr_seq_saida_consulta,0)
	into STRICT	nr_seq_saida_w
	from	conta_paciente
	where	nr_interno_conta = nr_interno_conta_p;

	select	max(cd_motivo_alta)
	into STRICT	ie_retorno_w
	from	tiss_motivo_alta
	where	nr_seq_saida_consulta = nr_seq_saida_w
	and (coalesce(dt_inicio_vigencia::text, '') = '' or dt_inicio_vigencia <= clock_timestamp());

elsif (ie_opcao_p = 'I') then

	select	coalesce(nr_seq_saida_int,0)
	into STRICT	nr_seq_saida_w
	from	conta_paciente
	where	nr_interno_conta = nr_interno_conta_p;

	select	max(cd_motivo_alta)
	into STRICT	ie_retorno_w
	from	tiss_motivo_alta
	where	nr_seq_saida_int = nr_seq_saida_w
	and (coalesce(dt_inicio_vigencia::text, '') = '' or dt_inicio_vigencia <= clock_timestamp());

elsif (ie_opcao_p = 'S') then

	select	coalesce(nr_seq_saida_spsadt,0)
	into STRICT	nr_seq_saida_w
	from	conta_paciente
	where	nr_interno_conta = nr_interno_conta_p;

	select	max(cd_motivo_alta)
	into STRICT	ie_retorno_w
	from	tiss_motivo_alta
	where	nr_seq_saida_spsadt = nr_seq_saida_w
	and (coalesce(dt_inicio_vigencia::text, '') = '' or dt_inicio_vigencia <= clock_timestamp());
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
